import { Link } from "react-router-dom";
import Header from "./Header";
import "../styles/Login.css";
import loginLogo from "../pictures/blood-donation-doctorPatient.png";
import { useState } from "react";
import { loginUser } from "../api/api"; 

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      const userData = await loginUser(email, password);
      console.log("Logged in user:", userData);
      // redirect or show success message
    } catch (error) {
      console.error("Login failed:", error.message);
    }
  };

  return (
    <>
      <Header />
      <div className="container">
        <div className="logo">
          <img src={loginLogo} alt="Logo" width="50" />
        </div>
        <h1 className="sign-up">Sign up</h1>
        <h1>Welcome back to BloodConnect</h1>
        <p>
          Back to save more lives?ðŸ©¸
          <br />
          Log in to BloodConnect below
        </p>
        <div className="google-btn">
          <img src="/assets/google-icon.png" alt="Google Icon" />
          Continue with Google
        </div>
        <form onSubmit={handleLogin}>
          <div className="input-group">
            <label htmlFor="email">Email</label>
            <input
              type="email"
              id="email"
              placeholder="enter email..."
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </div>
          <div className="input-group">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              placeholder="enter password..."
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>
          <button type="submit">Login</button>
        </form>
        <div className="footer">
          Don't have an account? <Link to="/signup">Sign up for Free</Link>
        </div>
      </div>
    </>
  );
};

export default Login;
